	public static readonly Random Rand = new();

	public static string RandomWord(int? maxValue = null)
	{
		var word = WordList.ChooseOne(maxValue);
		return char.ToUpper(word[0]) + word[1..];
	}

	public static T ChooseOne<T>(this List<T> list, int? maxValue = null)
	{
		if (list.IsEmpty()) throw new Exception("empty list");
		return list[Rand.Next(0, maxValue ?? list.Count)];
	}

